var db = require('../lib/db')
  , model = require('../lib/model')

db.connect('mongodb://'+(process.env.MONGO_HOST || 'localhost')+'/test');

describe('a record', function () {

	var r = new model.Record();
	
	describe('when calling save', function () {

		it('should not give us an error', function (done) {	

			r.save(function (err) {
				expect(err).toBeFalsy();
				done();
				db.disconnect();
			});

		});

	});

});
